export const Icon = ({ id, width, height, className, isHidden, children }) => {
  let item = ICON_DATA.itemsById[id];

  if (!item || isHidden) {
    return (
      <svg display="none" height="0" width="0">
        {children}
      </svg>
    );
  }

  const iconWidth = width || item.width || null;
  const iconHeight = height || item.height || null;
  const iconViewBox = item.viewBox ? item.viewBox.join(" ") : `0 0 ${iconWidth} ${iconHeight}`;

  return (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      version="1.1"
      preserveAspectRatio="xMinYMin"
      width={iconWidth}
      height={iconHeight}
      viewBox={iconViewBox}
      className={className}
    >
      <use xlinkHref={"#" + ICONS_PREFIX + "-" + id} />
    </svg>
  )
}

export const IconSource = () => {
  return (
    <Icon isHidden={true}>
      <defs>
        {ICON_DATA.ids.reduce((defs, fileId, fileIndex) => {
          const file = ICON_DATA.itemsById[fileId];
          return defs.concat(
            <g
              key={fileIndex}
              id={ICONS_PREFIX + "-" + fileId}
              dangerouslySetInnerHTML={{ __html: file.data }}
            ></g>
          );
        }, [])}
      </defs>
    </Icon>
  );
}